template.body:
{
  "text": "ðŸš¨ Alert: {alert_name}",
  "blocks": [
    {
      "type": "header",
      "text": {
        "type": "plain_text",
        "text": "ðŸš¨ Alert: {alert_name}"
      }
    },
    {
      "type": "section",
      "fields": [
        {
          "type": "mrkdwn",
          "text": "*Stream:*\n{stream_name}"
        },
        {
          "type": "mrkdwn",
          "text": "*Type:*\n{stream_type}"
        },
        {
          "type": "mrkdwn",
          "text": "*Count:*\n{alert_count}"
        },
        {
          "type": "mrkdwn",
          "text": "*Threshold:*\n{alert_operator} {alert_threshold}"
        }
      ]
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*Status:* ðŸ”´ Firing\n*Time:* {alert_time}"
      }
    },
    {
      "type": "actions",
      "elements": [
        {
          "type": "button",
          "text": {
            "type": "plain_text",
            "text": "View in OpenObserve"
          },
          "url": "{alert_url}"
        }
      ]
    }
  ]
}

{
  "@type": "MessageCard",
  "@context": "http://schema.org/extensions",
  "themeColor": "D63638",
  "summary": "Alert: {alert_name}",
  "sections": [
    {
      "activityTitle": "ðŸš¨ Alert: {alert_name}",
      "activitySubtitle": "OpenObserve Alert Notification",
      "activityImage": "https://openobserve.ai/favicon.ico",
      "facts": [
        {
          "name": "Stream",
          "value": "{stream_name}"
        },
        {
          "name": "Type",
          "value": "{stream_type}"
        },
        {
          "name": "Status",
          "value": "ðŸ”´ Firing"
        },
        {
          "name": "Count",
          "value": "{alert_count}"
        },
        {
          "name": "Threshold",
          "value": "{alert_operator} {alert_threshold}"
        },
        {
          "name": "Time",
          "value": "{alert_time}"
        }
      ],
      "markdown": true
    }
  ],
  "potentialActions": [
    {
      "@type": "OpenUri",
      "name": "View in OpenObserve",
      "targets": [
        {
          "os": "default",
          "uri": "{alert_url}"
        }
      ]
    }
  ]
}

{
  "content": "ðŸš¨ Alert: {alert_name}",
  "embeds": [
    {
      "title": "Alert Details",
      "color": 15158332,
      "fields": [
        {
          "name": "Stream",
          "value": "{stream_name}",
          "inline": true
        },
        {
          "name": "Type",
          "value": "{stream_type}",
          "inline": true
        },
        {
          "name": "Count",
          "value": "{alert_count}",
          "inline": true
        },
        {
          "name": "Threshold",
          "value": "{alert_operator} {alert_threshold}",
          "inline": true
        }
      ],
      "footer": {
        "text": "OpenObserve Alert",
        "icon_url": "https://openobserve.ai/favicon.ico"
      },
      "timestamp": "{alert_time}"
    }
  ]
}
